Ext.define('C.view.DistributionForm', {
	extend: 'Ext.form.Panel',

	border: '0 0 0 0',
	frame: false,
	margin: '10px',
	style: 'border: none',
	width: 170,
	layout: {
		type: 'auto'
	},
	bodyBorder: false,
	bodyCls: 'x-panel-mc',
	bodyPadding: 10,
	closable: false,
	collapsible: false,
	titleCollapse: false,
	standardSubmit: false,

	initComponent: function() {
		var me = this;

		me.initialConfig = Ext.apply({
			standardSubmit: false
		}, me.initialConfig);

		Ext.applyIf(me, {
			items: [
				{
					xtype: 'container',
					height: 326,
					padding: '10px',
					width: 151,
					items: [
						{
							xtype: 'textfield',
							width: 126,
							name: 'name'
						},
						{
							xtype: 'textfield',
							width: 126,
							name: 'type'
						},
						{
							xtype: 'textareafield',
							height: 41,
							width: 128,
							name: 'description'
						},
						{
							xtype: 'combobox',
							width: 128,
							name: 'distrType',
							readOnly: false,
							displayField: 'distrType',
							queryMode: 'local',
							store: 'DistrTypeStore',
							valueField: 'distrType'
						},
						{
							xtype: 'textareafield',
							height: 45,
							width: 129,
							name: 'val'
						},
						{
							xtype: 'textareafield',
							height: 67,
							width: 129,
							name: 'params'
						},
						{
							xtype: 'button',
							itemId: 'btn',
							margin: '10px 0 0 50px',
							text: 'Update',
							listeners: {
								click: {
									fn: me.onButtonClick2,
									scope: me
								}
							}
						}
					]
				}
			]
		});

		me.callParent(arguments);
	},

	onButtonClick2: function(button, e, options) {

		var myForm = this.getForm();
		var record = myForm.getRecord();
		var values = myForm.getValues();

		var parameters = myForm.getFieldValues().params;
		var valuesDistr = myForm.getFieldValues().val;
		var myConsModChartStore = this.query('chart')[0].store;

		if(parameters) {
			try {
				parameters = JSON.parse(parameters);
			}
			catch(e) {
				Ext.MessageBox.show({
					title:'"Parameters" field: Invalid input', 
					msg: 'A valid input example would be: </br>[{"w":103,"mean":203.3,"std":103.4}]', 
					icon: Ext.MessageBox.ERROR
				});
				return false;
			}
		}
		else
		parameters = [];

		if(valuesDistr) {
			try {
				valuesDistr = JSON.parse(valuesDistr);
			}
			catch(e) {
				Ext.MessageBox.show({
					title:'"Values" field: Invalid input', 
					msg: 'A valid input example would be: [3,4]', 
					icon: Ext.MessageBox.ERROR
				});
				return false;
			}
		}
		else 
		valuesDistr = [];

		if (record) {
			record.set({
				'name':values.name,
				'type': values.type,
				'description': values.description,
				'distrType': values.distrType,
				'parameters': parameters, 
				'values': valuesDistr
			});

			if (record.isNew)
			record.isNew = false;

			myConsModChartStore.removeAll();
			myConsModChartStore.load();

		}

		else {
			var propertiesCmp = Ext.getCmp('MainTabPanel').getActiveTab().query('form')[0];
			var actmod_record = propertiesCmp.getForm().getRecord();
			var distr_store = actmod_record.c.store;

			distr_store.insert(0,{
				name:values.name,
				type: values.type,
				description: values.description,
				distrType: values.distrType,
				values: valuesDistr, 
				parameters: parameters,
				actmod_id:actmod_record.get('_id')
			});

			distr_type = this.distr_type;

			distr_store.on(
			'update', 
			function(store, record, operation, eOpts ) {
				actmod_record.set(distr_type,record.get('_id') );
				myForm.loadRecord(record);
				myConsModChartStore.proxy.url += '/' + record.get('_id');
				myConsModChartStore.load();
			}, 
			null, 
			{ single : true }
			);							  
		}

		this.dirtyForm = false;
	}

});